(function() {
    var r = "";
    var s = Wikifier.formatters;
    for (var j = 0; j < s.length; j++) {
        if (s[j].name == "image") {
            r = s[j].lookahead;
            break;
        }
    }
    var div = document.getElementById("storeArea").firstChild.nextSibling;
    while (div) {
        if (r) {
            k(new RegExp(r, "mg"), 4);
        }
        var b = String.fromCharCode(92); // backslash
        var u = b + "s*['" + '"]?([^"' + "']+(jpe?g|a?png|gif|bmp))['" + '"]?' + b + "s*";
        k(new RegExp("url" + b + "(" + u + b + ")", "mig"), 1);
        k(new RegExp("src" + b + "s*=" + u, "mig"), 1);
        div = div.nextSibling;
    }

    function k(c, e) {
        do {
            var d = c.exec(div.innerHTML);
            if (d && /(jpe?g|a?png|gif|bmp)$/.exec(d[e])) {
                var i = new Image();
                i.src = d[e];
            }
        } while (d);
    }
}());